#input_directions = '^>v<'
print('Size of route is: ', len(input_directions))

# create an emtpy list to hold the coordinates where Santa has been
coords = []
# each coordinate point where Santa visits is a tuple... starting at the origin of (0,0)
start = (0, 0)

test_count=[]
new_position = list(start)
count=0
visited = [0, 0]
coords.extend(visited)

# movement
for charc in input_directions:
    count=count+1
    new_position = visited
    if charc == '^':
        visited[0] = new_position[0] + 0
        visited[1] = new_position[1] + 1
    if charc == '>':
        visited[0] = new_position[0] + 1
        visited[1] = new_position[1] + 0
    if charc == '<':
        visited[0] = new_position[0] - 1
        visited[1] = new_position[1] + 0
    if charc == 'v':
        visited[0] = new_position[0] + 0
        visited[1] = new_position[1] - 1
    coords.extend(visited)
    test_count.append(count)

finalcoords = []

#code from stackoverload to get every two items from a list to populate another list
N = 2
subcoords = [coords[n:n+N] for n in range(0,len(coords), N)]

#create final list of coordinates
for item in subcoords:
    if not item in finalcoords:
        finalcoords.append(item)
print('Count of destination..', len(finalcoords))


